#include <fstream>
#include <iostream>
#include <string>
using namespace std;
 
int main () {
    int numArray[999];
    unsigned long long int currMax = 0;
    
    string number = "";
    
    for(std::string::size_type i = 0; i < number.size(); ++i) {
        numArray[i] = (int)number[i] - 48;

    }

    for(int start_i = 0; start_i < number.size() - 12; ++start_i){
        unsigned long long int local_max = 1;
        for(int calc_i = 0; calc_i < 13; ++calc_i){
            local_max = local_max * numArray[calc_i + start_i];
        }
        if(local_max > currMax){
            currMax = local_max;
        }
        
    }
    cout << "Answer : ";
    cout << currMax;
    cout << "\n";
    return 0;
}
